import React, { Component } from 'react';
import './index.css';
import EventVisibilitySettingsBtn from './components/EventVisibilitySettingsBtn.js';
import ImageSection from './components/ImageSection.js';
import EventTitle from './components/EventTitle.js';
import Location from './components/Location.js';
import Schedule from './components/Schedule.js';
import Description from './components/Description.js';
import SubmitEventBtn from './components/SubmitEventBtn';


export default class CreateEventPage extends React.Component{
      constructor(props){
            super(props);
            this.state = {
                  backgroundImage:"http://r.ddmcdn.com/s_f/o_1/cx_462/cy_245/cw_1349/ch_1349/w_720/APL/uploads/2015/06/caturday-shutterstock_149320799.jpg",
                  title:'',
            }
            this.changeTitle = this.changeTitle.bind(this);
            this.uploadImage = this.uploadImage.bind(this);
            this.changeLocation = this.changeLocation.bind(this);


      }

      changeTitle(e){
            this.setState({
                  title:e.target.value
            })
      }

      uploadImage(e){
            this.setState({
                  backgroundImage: e.target.files[0]
            })

      }

      render(){
          console.log(this.state);

            return(
                 <div className='CreateEventPageWrap' style={styles.CreateEventPageWrap}>
                        <ImageSection 
                              backgroundImage={this.state.backgroundImage }
                              uploadImage={this.uploadImage}
                        />
                        <EventTitle 
                              changeTitle={this.changeTitle} 
                              title={this.state.title}
                        />
                        <Location 
                              changeLocation={this.changeLocation}

                        />
                 </div>
            )
      }
}

const styles = {
      CreateEventPageWrap:{
            width:700,
            margin:'auto',
            display:'block',
            backgroundColor:'gray',
            padding:20,
            marginTop: 30,
            marginBottom: 30,
      }
}
